/**
 * Get human-readable name for an item ID
 */
export function getItemName(itemId: number): string {
  return ITEM_NAMES[itemId] ?? `ITEM ${itemId}`;
}

/**
 * Get item ID from constant name (e.g., "ITEM_POTION" -> 13)
 */
export function getItemId(constantName: string): number | null {
  return ITEMS[constantName] ?? null;
}

